package org.springcourse.SensorAPI.models;import jakarta.persistence.*;import java.time.LocalDateTime;@Entity@Table(name = "measurement")public class Measurement {    @Id    @Column(name = "id")    @GeneratedValue(strategy = GenerationType.IDENTITY)    private int id;    @ManyToOne    @JoinColumn(name = "sensor_name", referencedColumnName = "name")    private Sensor sensor;    @Column(name = "value")    private double value;    @Column(name = "is_raining")    private boolean isRaining;    @Column(name = "created_at")    private LocalDateTime createdAt;    public Measurement() {    }    public Measurement(int id, Sensor sensor, double value, boolean isRaining, LocalDateTime createdAt) {        this.id = id;        this.sensor = sensor;        this.value = value;        this.isRaining = isRaining;        this.createdAt = createdAt;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public Sensor getSensor() {        return sensor;    }    public void setSensor(Sensor sensor) {        this.sensor = sensor;    }    public double getValue() {        return value;    }    public void setValue(double value) {        this.value = value;    }    public boolean getIsRaining() {        return isRaining;    }    public void setIsRaining(boolean raining) {        isRaining = raining;    }    public LocalDateTime getCreatedAt() {        return createdAt;    }    public void setCreatedAt(LocalDateTime createdAt) {        this.createdAt = createdAt;    }}